(function(ng) {
    ng.module('BlueMirrorApp').controller('TodoController', function($state, $scope, $q, DataRequestService, UserService) {

        $scope.currentUser = UserService.getUser();

        $scope.todoObj = {
            todo: ''
        };

        $scope.todos = [];
        $scope.randomArray = [];

        // total todos
        $scope.totalToDo = function() {
            return $scope.todos.length;
        };

        // add todos
        $scope.addToDo = function() {
            $scope.todoObj.todo = $scope.input;
            $scope.error = '';
            $scope.input = '';
        };

        // get todos
        $q.when(DataRequestService.get('/todos')).then((response) => {

            $scope.defaultTodos = response.data;

            for (var todo in $scope.defaultTodos) {
                $scope.allTodos = $scope.defaultTodos[todo];
                $scope.todos.push($scope.allTodos);
            }


        }).catch((error) => {
            console.log(error);
        });

        //get others' todos
        $scope.othersTodos = function() {
            $q.when(DataRequestService.get('/todos/featured')).then((response) => {
                let data = response.data;
                $scope.shuffle(data);
            }).catch((error) => {
                console.log(error);
            });
        };

        $scope.clearOthers = function() {
            $scope.randomArray = [];
        };

        //shuffle and push featured to dos
        $scope.shuffle = function(data) {

            for (let i = data.length - 1; i > 0; i--) {
                let j = Math.floor(Math.random() * (i + 1));
                let temp = data[i];
                data[i] = data[j];
                data[j] = temp;
            }
            for (let i = 0; i < 5; i++) {
                $scope.randomArray.push(data[i]);
            }

        };
        // post todos
        $scope.postTodos = function() {

            if ($scope.input === undefined || $scope.input === '') {

                $scope.error = "Please enter a task";

            } else {
                $scope.addToDo();

                $q.when(DataRequestService.postTodo('/todos', $scope.todoObj)).then((response) => {
                    $scope.currentTodos = response.data.location;
                    $scope.todos.push($scope.currentTodos);

                }).catch((error) => {
                    console.log(error);
                });
            }

        };

        // delete todos
        $scope.deleteTodos = function() {

            for (let i = $scope.todos.length - 1; i >= 0; i--) {

                if ($scope.todos[i].done === true) {
                    $q.when(DataRequestService.delete(`/todos/${$scope.todos[i].id}`)).then((response) => {

                    }).catch((error) => {
                        console.log(error);
                    });

                    $scope.todos.splice(i, 1);
                }
            }
        };
    });

})(angular);
(function(ng, currentUser) {
    ng.module('BlueMirrorApp').service('DataRequestService', AllDataService);

    function AllDataService($http) {
        function getData(url) {
            return $http({
                method: 'GET',
                url: url
            });
        }

        function postData(url, dataObj) {
            return $http({
                method: 'POST',
                url: url,
                dataType: "json",
                headers: {
                    "content-type": "application/json;charset=utf-8"
                },
                data: dataObj
            });
        }

        function postTodo(url, todoObj) {
            return $http({
                method: 'POST',
                url: url,
                dataType: "json",
                headers: {
                    "content-type": "application/json;charset=utf-8"
                },
                data: todoObj
            });
        }

        function patchData(url, moodList) {
            return $http({
                method: 'PATCH',
                url: url,
                dataType: "json",
                headers: {
                    "content-type": "application/json;charset=utf-8"
                },
                data: moodList
            });
        }

        function patchNumber(url, number, provider, frequency) {
            return $http({
                method: 'PATCH',
                url: url,
                headers: {
                    "content-type": "application/json;charset=utf-8"
                },
                data: {
                    phone: number,
                    phone_provider: provider,
                    sms_frequency: frequency
                }
            });
        }

        function patchEntry(url, inputText, inputTitle, inputTag) {
            return $http({
                method: 'PATCH',
                url: url,
                dataType: "json",
                headers: {
                    "content-type": "application/json;charset=utf-8"
                },
                data: {
                    text: inputText,
                    title: inputTitle,
                    tags: inputTag

                }
            });
        }

        function flipPage(url) {
            return $http({
                method: 'GET',
                url: url
            });

        }

        function postJournal(url, journalObj) {
            return $http({
                method: 'POST',
                url: url,
                dataType: "json",
                headers: {
                    "content-type": "application/json;charset=utf-8"
                },
                data: journalObj
            });
        }

        function deleteData(url) {
            return $http({
                method: 'DELETE',
                url: url,
                headers: {
                    "content-type": "application/json;charset=utf-8"
                },
            });
        }

        return {
            get: getData,
            flipPage: flipPage,
            post: postData,
            postTodo: postTodo,
            postJournal: postJournal,
            patchEntry: patchEntry,
            patchNumber: patchNumber,
            delete: deleteData,
            patch: patchData
        };
    }


})(angular, window.currentUser);
